public class variables {
    
    
    public static void main(String[] args)
    {
        		// TODO Auto-generated method stub
     Boolean();  //Calling methods that are outside of main method so that they show 
     Char();     // the results. 
     Byte();
     Short();
     Int ();
     Long ();
     Float ();
     Double ();
     String ();
	}

public static void Boolean()	//Declaring a 2nd method(function) called Boolean
{
	boolean a = true;	//Syntax to declare a boolean in Java
	boolean b = false;	//Booleans can have only two values i.e, true & false
	
	System.out.println("Value of Boolean a is " +a); //syntax to print a line
	System.out.println("Value of Boolean b is " +b);
}

public static void Char()	
{
	char a ='B';	// Syntax to declare a character in Java
	System.out.println("Value of Char a is " +a);
}

public static void Byte()
{
	byte a = 2;	// Syntax to declare a byte in Java
	System.out.println("Value of Byte a is " +a);
}

public static void Short()
{
	short a = 2;	//Syntax to declare a short in Java
	System.out.println("Value of Short a is " +a);
}

public static void Int()
{
	int a = 225;	//Syntax to declare a integer in Java
	System.out.println("Value of int a is " +a);
}

public static void Long()
{
	long a = 2;	//Syntax to declare a long in Java
	System.out.println("Value of Long a is " +a);
}

public static void Float()
{
	float a = (float)2.5;	//Syntax to declare a float in Java
	System.out.println("Value of Float a is " +a);
}

public static void Double()
{
	double a = 125;	//Syntax to declare a double in Java
	System.out.println("Value of Double a is " +a);
}

public static void String()
{
	String a = "Sayantan";	//Syntax to declare a string in Java
	System.out.println("Value of string a is \n " +a);
}
    
}

